import { reqUserList, userDelete, userUpdate, userAdd } from '@/api/user'
import md5 from 'md5'
import { notification } from 'ant-design-vue'

const columns = [
  {
    title: 'id',
    dataIndex: 'id',
    key: 'id',
    show: false
  },
  {
    title: '用户名',
    key: 'login_name',
    dataIndex: 'login_name'
  },
  {
    title: '姓名',
    key: 'name',
    dataIndex: 'name'
  },
  // {
  //   title: '角色',
  //   key: 'role_type',
  //   dataIndex: 'role_type'
  // },
  {
    title: '操作',
    key: 'action',
    scopedSlots: { customRender: 'action' }
  }
]
export default {
  name: 'Dashboard',
  data() {
    return {
      data: null,
      columns,
      page_num: 1,
      page_size: 10,
      total: 0,
      // 创建、修改
      createUserModal: false,
      confirmLoading: false,
      type: '',
      isChangePassword: false,
      form: this.$form.createForm(this, {
        id: null,
        login_name: null,
        name: null,
        role_type: '普通用户',
        password: null,
        confirm_password: null
      })
    }
  },
  methods: {
    compareToFirstPassword(rule, value, callback) {
      const form = this.form
      if (value && value !== form.getFieldValue('password')) {
        callback('两次密码不同，请重新输入!')
      } else {
        callback()
      }
    },
    async handlePageChange(page_num, page_size) {
      let res = await reqUserList({ page_num: page_num, page_size: page_size })
      this.data = res.result.users
      this.total = res.result.count
    },
    closeModal() {
      this.createUserModal = false
      this.isChangePassword = false
      this.form = this.$form.createForm(this, {
        id: null,
        login_name: null,
        name: null,
        role_type: '普通用户',
        password: null,
        confirm_password: null
      })
    },
    showCreateUserModal() {
      this.createUserModal = true
      this.type = 'create'
      this.form = this.$form.createForm(this, {
        id: null,
        login_name: null,
        name: null,
        role_type: '普通用户',
        password: null,
        confirm_password: null
      })
    },
    showUpdateUserModal(record) {
      this.createUserModal = true
      this.type = 'update'
      this.isChangePassword = false
      this.form = this.$form.createForm(this, {
        props: {
          id: Number,
          login_name: record.login_name,
          name: record.name,
          role_type: '普通用户',
          password: null,
          confirm_password: null
        }
      })
      const that = this
      this.$nextTick(() => {
        that.form.setFieldsValue({
          id: record.id,
          login_name: record.login_name,
          name: record.name
        })
      })
    },
    async getTableData() {
      let res = await reqUserList({ page_num: 1, page_size: 10 })
      this.data = res.result.users
      this.total = res.result.count
      this.page_num = res.result.page_num
      this.page_size = res.result.page_size
    },
    submitForm() {
      this.form.validateFields(async (err, values) => {
        if (!err) {
          console.log(values)
          let res
          let success
          let error
          if (values.password) {
            values.password = md5(values.password)
          }
          if (this.type === 'create') {
            res = await userAdd(values)
            success = '创建成功'
            error = '创建失败'
          } else {
            res = await userUpdate(values)
            success = '更新成功'
            error = '更新失败'
          }
          if (res.code === 200) {
            notification['success']({
              message: success
            })
            this.getTableData()
            this.closeModal()
          } else {
            notification['error']({
              message: error
            })
          }
        }
      })
    },
    deleteUser(id) {
      const that = this
      this.$confirm({
        title: '确定要删除该用户吗?',
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        async onOk() {
          let res = await userDelete({ id: id })
          if (res.code === 200) {
            that.$notification.open({
              message: '删除成功',
              duration: 1
            })
            // this.getTableData()
            // this.closeDialog()
            let res = await reqUserList({ page_num: 1, page_size: 10 })
            that.data = res.result.users
            that.total = res.result.count
            that.page_num = res.result.page_num
            that.page_size = res.result.page_size
          }
        },
        onCancel() {
          console.log('Cancel')
        }
      })

    }
  },
  async created() {
    let res = await reqUserList({ page_num: 1, page_size: 10 })
    this.data = res.result.users
    this.total = res.result.count
    this.page_num = res.result.page_num
    this.page_size = res.result.page_size
  }
}
